/* date */
function date(field) {
  "use strict";
  /* Try to fix (convert to yyyy-mm-dd) and validate. */

  /* Support functions */
  function str(date) {return date instanceof Date ? date.toJSON().split('T')[0] : '';}

  function norm_day(day) {
    if (day.length == 2) return day;
    else if (day.length == 1) return '0' + day;
    return '01';
  }

  function norm_year(year) {
    var fy = (new Date()).getFullYear();
    if (!year) return fy;

    if (year.length == 4) return year;

    var
      mol = 10 * year.length,
      y = parseInt(year);
    if ( y < fy % mol || y < (fy + Math.floor(mol/4) % mol)) { // 4 - A magic number ?;)
      return '' + (Math.floor(fy/mol) * mol + year); // Current period
    } else {
      return '' + ((Math.floor(fy/mol) - 1) + year);  // Last period
    }
  }

  function norm(value) {
    // Try to fix complete form
    var token = value.split(/[^0-9]/);
    if (ymd.test(value)) return value;
    else if (dmy.test(value)) return token[2]+'-'+token[1]+'-'+token[0];
    else if (mdy.test(value)) return token[2]+'-'+token[0]+'-'+token[1];
    else return false;
  }

  var // Common format (length == 10)
    ymd = /^(19|20)\d\d[- /.](0[1-9]|1[012])[- /.](0[1-9]|[12][0-9]|3[01])$/,
    dmy = /^(0[1-9]|[12][0-9]|3[01])[- /.](0[1-9]|1[012])[- /.](19|20)\d\d$/ ,
    mdy = /^(0[1-9]|1[012])[- /.](0[1-9]|[12][0-9]|3[01])[- /.](19|20)\d\d$/ ;

  var value = field.value,
      elms = value.split(/[^0-9]/);

  // Try to fix before going ahead.
  if (value.length  < 10) {
    // Short date d-m-yy eg. 1-11-93
    if (elms.length == 3) {
      var
        // ymd
        t1 = norm_year(elms[0]) + '-' + norm_day(elms[1]) + '-' + norm_day(elms[2]),
        // (md|dm)y
        t2 =  norm_day(elms[0]) + '-' + norm_day(elms[1]) + '-' + norm_year(elms[2]);

      if (value = norm(t1)) ;
      else if (value = norm(t2)) ;

    } else if (elms.length == 2) {
      // Missing year ?
      var t = norm_day(elms[0]) + '-' + norm_day(elms[1]) + '-' + norm_year(false);
      // try - nothing to loose.
      value = norm(t);
      // normalize input data
      elms = value.split(/[^0-9]/);
    }
  } else {
    value = norm(value);
  }

  // Value should now be fixed.
  // Test last time with new Date()
  var date = new Date(value);

  if (!date || isNaN(date)) {
    // Give up. Not valid date format
    field.value = '';
    console.error('not valid date: ' + value);
    return false;
  } else {
    field.value = str(date);
  }

  // Check boundary (if any)
  var
    min = new Date(field.min),
    max = new Date(field.max);
  if (!isNaN(min)) {
    if (date < min) {
      console.log('Min boundary not respected');
      return false;
    }
  } else if (field.min.length > 0) {
    console.warn('Min boundary date format not recognized: ' + field.min);
  }

  if (!isNaN(max)) {
    if (date > max) {
      console.log('Max boundary not respected');
      return false;
    }
  } else if (field.max.length > 0) {
    console.warn('Max boundary date format not recognized: ' + field.max);
  }

  return true;
}

/* Modal support */
function open_modal(e) {
    e.preventDefault();
    var page_url = $(e.target).attr('href');
    $.get(page_url, function (data, status) {
        if (['success', 'notmodified'].indexOf(status) === -1) {
            console.error("Error " + status + " loading " + page_url);
            return;
        }
        $('#modal section[name=modal-content]').html(data);
        $('#modal').addClass('active');
    });
}

function close_modal(e) {
    e.preventDefault();
    if (e.target === $('#modal').get(0) || e.target === $('#modal a[data-action=close]').get(0)) {
        $('#modal').removeClass('active');
    }
}

/* maps - route */
function init_gmap() {
    var map,
        marker,
        point = new google.maps.LatLng(46.027988, 11.232662),
        mapOptions = {
            zoom: 14,
            center: point,
            scrollwheel: false,
            mapTypeControl: true,
            mapTypeControlOptions: {
                style: google.maps.MapTypeControlStyle.DROPDOWN_MENU
            },
            zoomControl: true,
            zoomControlOptions: {
                style: google.maps.ZoomControlStyle.SMALL
            },
            mapTypeId: google.maps.MapTypeId.ROADMAP
        };
    map = new google.maps.Map(document.getElementById('map'),
        mapOptions);
    marker = new google.maps.Marker({
        map: map,
        draggable: false,
        animation: google.maps.Animation.DROP,
        position: point,
        raiseOnDrag: true,
        title: "Camping Punta Indiani"
    });

    // support resize
    window.last_map_size = $('#map').width();
    $(window).on('resize', (function (map, center) {
        return function () {
            if (window.last_map_size === $('#map').width()) {
                return;
            }
            window.last_map_size = $('#map').width();
            map.setCenter(center);
        };
    }(map, point)));

    // animation
    function toggleBounce() {
        if (marker.getAnimation() != null) {
            marker.setAnimation(null);
        } else {
            marker.setAnimation(google.maps.Animation.BOUNCE);
        }
    }
    google.maps.event.addListener(marker, 'click', toggleBounce);
}

